import React from "react";
export default class RemoveTodo extends React.Component {

  removeItem(id) {
    this.setState({
      data: this.state.data.filter((_, i) => i !== id)
    });
  }

  render() {
    const { value, handlerChange } = this.props;
    return (
      <div>
        <input placeholder="search" value={value} onChange={handlerChange} />
      </div>
    );
